mapper.select:
select 

from SHOP


    and SHOP_ID = #{shopId,jdbcType=VARCHAR}


    and SHOP_NAME= #{shopName,jdbcType=VARCHAR}


    and CLASS_ID= #{classId,jdbcType=VARCHAR}


    and IS_HOT= #{isHot,jdbcType=VARCHAR}


    and ADMIN_USER_ID= #{adminUserId,jdbcType=VARCHAR}

   and IS_DELETE='0'
